const getSectionContent = `-- name: GetSectionContent :one
SELECT 
    CASE s.type
        WHEN 'text' THEN (
            SELECT jsonb_build_object('text', text_content)
            FROM text_sections
            WHERE section_id = s.id
        )
        WHEN 'video' THEN (
            SELECT jsonb_build_object('url', url)
            FROM video_sections
            WHERE section_id = s.id
        )
        WHEN 'question' THEN (
            SELECT jsonb_build_object(
                'id', q.id,
                'question', q.question,
                'type', q.type,
                'options', (
                    SELECT jsonb_agg(jsonb_build_object(
                        'id', qo.id,
                        'content', qo.content,
                        'isCorrect', qo.is_correct
                    ))
                    FROM question_options qo
                    WHERE qo.question_id = q.id
                )
            )
            FROM question_sections qs
            JOIN questions q ON q.id = qs.question_id
            WHERE qs.section_id = s.id
        )
        WHEN 'code' THEN (
            SELECT jsonb_build_object(
                'code', code, 
                'language', language
            )
            FROM code_sections
            WHERE section_id = s.id
        )
    END as content
FROM sections s
WHERE s.id = $1::int
`

func (q *Queries) GetSectionContent(ctx context.Context, sectionID int32) (interface{}, error) {
	row := q.db.QueryRowContext(ctx, getSectionContent, sectionID)
	var content interface{}
	err := row.Scan(&content)
	return content, err
}

